mapper.select:
select * from board where category = #{category}
order by
boardnum desc

mapper.update:
update likecheck
set likecount = likecount + 1
where userid=#{userid} and boardnum=#{boardnum}

update likecheck
set likecount = 0
where userid=#{userid} and boardnum=#{boardnum}

mapper.select:
select count(likenum) as likecount 
from likecheck
where boardnum=#{boardnum} and userid=#{userid}
